import React from 'react';

function ModelModal({ model, onClose, resource }) {
  if (!model) return null;

  return (
    <div style={overlayStyle} onClick={onClose}>
      <div style={modalStyle} onClick={(e) => e.stopPropagation()}>
        <button style={closeBtnStyle} onClick={onClose}>‚ùå Close</button>
        <h2>{model.Algorithm}</h2>
        <p><strong>Type:</strong> {model.Type}</p>
        <p><strong>Best Use Case:</strong> {model['Best Use Case']}</p>
        <p><strong>Key Fundamentals:</strong> {model['Key Fundamentals to Know']}</p>
        <p><strong>Libraries:</strong> {model.Libraries}</p>
        <p><strong>Real-World Example:</strong> {model['Real-World Example']}</p>

        {/* External resources */}
        {resource?.Medium && (
          <p>üìò <a href={resource.Medium} target="_blank" rel="noreferrer">Read on Medium</a></p>
        )}
        {resource?.GitHub && (
          <p>üíª <a href={resource.GitHub} target="_blank" rel="noreferrer">View GitHub Notebook</a></p>
        )}

        {/* Optional fallback */}
        {!resource && (
          <p style={{ color: 'orange' }}>üöß Learning content coming soon!</p>
        )}
      </div>
    </div>
  );
}

// Styles
const overlayStyle = {
  position: 'fixed',
  top: 0, left: 0, right: 0, bottom: 0,
  backgroundColor: 'rgba(0,0,0,0.5)',
  display: 'flex', justifyContent: 'center', alignItems: 'center',
  zIndex: 9999
};

const modalStyle = {
  background: '#fff',
  padding: '2rem',
  borderRadius: '8px',
  maxWidth: '600px',
  maxHeight: '80vh',
  overflowY: 'auto',
  position: 'relative'
};

const closeBtnStyle = {
  position: 'absolute',
  top: '10px', right: '10px',
  background: 'red',
  color: '#fff',
  border: 'none',
  padding: '0.4rem 1rem',
  borderRadius: '5px',
  cursor: 'pointer'
};

export default ModelModal;
