import React, { useEffect, useState } from 'react';
import SearchBar from './components/SearchBar';
import ModelCard from './components/ModelCard';
import FilterSidebar from './components/FilterSidebar';
import ModelModal from './components/ModelModal';

function App() {
  const [models, setModels] = useState([]);
  const [filtered, setFiltered] = useState([]);
  const [resourceMap, setResourceMap] = useState({});
  const [selectedModel, setSelectedModel] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      const base = await fetch(process.env.PUBLIC_URL + '/Complete_ML_AI.json');
      const resources = await fetch(process.env.PUBLIC_URL + '/resource_links.json');

      const baseData = await base.json();
      const resourceData = await resources.json();

      const resourceLookup = {};
      resourceData.forEach((item) => {
        if (item.show === 'Y') {
          resourceLookup[item.Algorithm] = item;
        }
      });

      const filteredModels = baseData.filter(m => resourceLookup[m.Algorithm]);

      setModels(filteredModels);
      setFiltered(filteredModels);
      setResourceMap(resourceLookup);
    };

    fetchData();
  }, []);

  const handleSearch = (query) => {
    if (!query) return setFiltered(models);
    const q = query.toLowerCase();
    setFiltered(models.filter((m) =>
      Object.values(m).some((val) =>
        val.toLowerCase().includes(q)
      )
    ));
  };

  return (
    <div style={{ display: 'flex' }}>
      <FilterSidebar data={models} setFiltered={setFiltered} />
      <div style={{ flex: 1, padding: '1rem' }}>
        <h1>ðŸ§  AI/ML Model Explorer</h1>
        <SearchBar onSearch={handleSearch} />
        <div style={{ display: 'flex', flexWrap: 'wrap', gap: '1rem' }}>
          {filtered.map((model, idx) => (
            <ModelCard
              key={idx}
              model={model}
              onClick={() => setSelectedModel(model)}
            />
          ))}
        </div>
        {selectedModel && (
          <ModelModal
            model={selectedModel}
            resource={resourceMap[selectedModel.Algorithm]}
            onClose={() => setSelectedModel(null)}
          />
        )}
      </div>
    </div>
  );
}

export default App;
